use futures::stream::Stream;
use async_stream::try_stream;
// use tracer::utils::u256_to_ru256;
// use tracer::tracer::execute_block;



fn get_new_blocks(client: Arc<Provider<Http>>, from: Option<U64>, to: Option<U64>) -> impl Stream<Item = Result<u64>> {
    
    try_stream! {
        let mut last_block = client.get_block_number().await?;
        let mut scanning_block = from.unwrap_or(last_block);

        loop {

            if let Some(to) = to {
                if scanning_block > to {
                    return;
                }
            }
            if scanning_block == last_block {
                tokio::time::sleep(Duration::from_secs(3)).await;
                last_block = client.get_block_number().await?;
            } else {
                yield scanning_block.as_u64();
                scanning_block += U64::from(1u64);
            }

        }
        
    }
}


#[tokio::main]
async fn main() -> Result<()> {

    let logger_level = LevelFilter::INFO;

    let filter = EnvFilter::from_default_env()
    .add_directive(logger_level.into());

    FmtSubscriber::builder()
        .with_env_filter(filter)
        // .with_writer(std::io::stderr)
        .init();

    let proxy_code = hex_literal::hex!("");

    let proxy_code = hex_literal::hex!("");

    // https://arbiscan.io/address/0xba169cceccf7ac51da223e04654cf16ef41a68cc#code TransparentUpgradeableProxy
    let proxy_code = hex_literal::hex!("");
    let proxy = get_proxy_type(&proxy_code);
    println!("proxy: {:?}", proxy);

    Ok(())
}

